	if (cardRoot.get("deleted") == null)
	{
		count = count + 1;
		//date is in order and attribute not duplicated  
		def quote = quoteBase + i.toString();
		def quote_author = quoteAuthorBase + i.toString();
		def charname = CharnameBase + i.toString();
